using System.Collections;using System.Collections.Generic;using UnityEngine;public class BallParticleRotation : MonoBehaviour {    public Rigidbody sphereRigidbody;    private Vector3 angularVelocity;	// Use this for initialization	void Start () {        angularVelocity = new Vector3(0, 0, 0);	}		// Update is called once per frame	void Update () {        angularVelocity = sphereRigidbody.angularVelocity * 2;// Time.deltaTime;        transform.Rotate(angularVelocity * Time.deltaTime);        //float scale = 1-(.98f * Time.deltaTime);        //angularVelocity.Scale(Vector3.one*scale);    }}